import React, { useState } from "react";
import * as $ from '../../CommonFunction';

const DatabaseRegist = () => {
    const [rows, initRow] = useState([]);
    const addRowTable = () => {
        const data = {
            columnName: '',
            columnNameJp: '',
            columnType: '',
            columnDigits: '',
            columnIsPk: '',
            columnIsNN: '',
            columnIsAi: '',
            columnDefault: ''
        }
        initRow([...rows, data]);
    }

    const tableRowRemove = (index = null) => {
        const dataRow = [...rows];
        if(index != null){
            dataRow.splice(index, 1);
        }else{
            dataRow.splice(dataRow.length-1, 1);
        }
        initRow(dataRow);
    }

    const onValueUpdate = (i, event) => {
        const {name, value} = event.target;
        const data = [...rows];
        data[i][name] = value;
        initRow(data);
        console.log(data);
    }

    const registDatabase = () => {
        $.CommonFunction.validateBlank();
    }

    return (
        <>
            <div className="title-area">
                データベース管理 - 新規登録
            </div>
            <div className="content-box">
                <div className="content-area">
                    <div className="input-table">
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 論理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableName"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 物理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableNameJp"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コラム</div>
                            <div className="column-td">
                                <div id="column-table" className="inner-table">
                                    <div className="inner-table-th">
                                        <span className="w-5"></span>
                                        <span className="w-15">論理名</span>
                                        <span className="w-15">物理名</span>
                                        <span className="w-10">タイプ</span>
                                        <span className="w-10">桁数</span>
                                        <span className="w-5">PK</span>
                                        <span className="w-5">FK</span>
                                        <span className="w-5">NN</span>
                                        <span className="w-5">AI</span>
                                        <span className="w-15">Default</span>
                                    </div>
                                    <ColumnTr
                                        rows={rows}
                                        tableRowRemove={tableRowRemove}
                                        onValueUpdate={onValueUpdate}
                                    />
                                </div>
                                <input type="button" value="追加" className="f-right ml-5" onClick={() => addRowTable()}/>
                                <input type="button" value="削除" className="f-right" onClick={() => tableRowRemove()}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="bottom-btn-area">
                <input type="button" value="登録" onClick={() => registDatabase()}/>
                <input type="button" value="戻る"/>
            </div>
        </>
    );
}

const ColumnTr = ({rows, tableRowRemove, onValueUpdate}) => {
    return rows.map((rowsData, index) => {
        const {columnIndex} = rowsData;
        return (
            <div className="inner-table-tr" key={index}>
                <span className="w-5"><input type="checkBox" name="columnIndex" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnName" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnNameJp" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-10">
                    <select className="w-80" name="columnType" onChange={(event) => onValueUpdate(index, event)}>
                        <option>int</option>
                        <option>varchar</option>
                        <option>datetime</option>
                    </select>
                </span>
                <span className="w-10"><input type="text" name="columnDigits" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" name="columnIsPk" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" name="columnIsFk" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" name="columnIsNn" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" name="columnIsAi" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnDefault" onChange={(event) => onValueUpdate(index, event)}/></span>
            </div>
        );
    });
}

export default DatabaseRegist;
