import React, { useEffect, useState } from "react";
import * as bean from "../../bean/BeanCollector";

const DatabaseRegist = () => {
    const [formData, setFormData] = useState(bean.MTable);
    const [rows, initRow] = useState([{
        tableColumnIndex: false,
        tableColumnName: '',
        tableColumnNameJp: '',
        tableColumnType: '',
        tableColumnDigits: '',
        tableColumnIsPk: false,
        tableColumnIsFk: false,
        tableColumnIsNn: false,
        tableColumnIsAi: false,
        tableColumnDefault: ''
    }]);
    const [formError, setFormError] = useState({
        error : false,
        errorObj : [
            {
                key : '',
                message : ''
            }
        ]
    })

    useEffect(() => {
        console.log(formData)
        validateInstant(formData);
        return () => {
            
        };
    },[formData]);

    const addRowTable = () => {
        const data = {
            tableColumnIndex: false,
            tableColumnName: '',
            tableColumnNameJp: '',
            tableColumnType: '',
            tableColumnDigits: '',
            tableColumnIsPk: false,
            tableColumnIsFk: false,
            tableColumnIsNn: false,
            tableColumnIsAi: false,
            tableColumnDefault: ''
        }
        initRow([...rows, data]);
        observeColumnIndex([...rows, data]);
    }

    const tableRowRemove = () => {
        const dataRow = [...rows];
        var newData = [];
        for(var i = 0; i < dataRow.length; i++){
            if(!dataRow[i].tableColumnIndex){
                newData.push(dataRow[i]);
            }
        }
        initRow(newData);
        observeColumnIndex(newData);
    }

    const onTableValueUpdate = (event) => {
        setFormData((prevState) => {
            var prevObj = {...prevState};
            prevObj[event.target.name] = event.target.value;
            return prevObj;
        })
    }

    const onRowValueUpdate = (i, event) => {
        const name = event.target.name;
        var value;
        if(event.target.type === 'checkbox'){
            value = event.target.checked;
        }else{
            value = event.target.value;
        }
        const data = [...rows];
        data[i][name] = value;
        observeColumnIndex(data);
        initRow(data);
        setFormData((prevState) => {
            var prevObj = {...prevState};
            prevObj['mTableColumn'] = data;
            return prevObj;
        });
    }

    const checkAll = (event) => {
        const data = [...rows];
        for(var i = 0;i < data.length;i++){
            data[i]['tableColumnIndex'] = event.target.checked;
        }
        initRow(data);
    }

    const observeColumnIndex = (data) => {
        var allCheckbox = document.getElementById('checkAll').checked;
        var isCheckAll = true;
        if(data.length === 0){
            isCheckAll = false;
        }
        for(var i = 0;i < data.length;i++){
            if(data[i]['tableColumnIndex'] === false){
                isCheckAll = false;
            }
            if(!isCheckAll) break;
        }

        if(allCheckbox && !isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
        }else if(!allCheckbox && isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
