import React, { useEffect, useState } from "react";
import * as $ from '../../CommonFunction';

const DatabaseRegist = () => {
    const [rows, initRow] = useState([]);

    useEffect(() => {
        addRowTable();
        return () => {
            
        };
    },[]);

    const addRowTable = () => {
        const data = {
            columnIndex: false,
            columnName: '',
            columnNameJp: '',
            columnType: '',
            columnDigits: '',
            columnIsPk: false,
            columnIsFk: false,
            columnIsNN: false,
            columnIsAi: false,
            columnDefault: ''
        }
        initRow([...rows, data]);
    }

    const tableRowRemove = () => {
        const dataRow = [...rows];
        for(var i = 0; i < dataRow.length; i++){
            if(dataRow[i].columnIndex){
                dataRow.splice(i, 1);
            }
        }
        initRow(dataRow);
    }

    const onValueUpdate = (i, event) => {
        const name = event.target.name;
        var value;
        if(event.target.type === 'checkbox'){
            value = event.target.checked;
        }else{
            value = event.target.value;
        }
        const data = [...rows];
        data[i][name] = value;
        initRow(data);
    }

    const checkAll = (event) => {
        console.log(event);
        const data = [...rows];
        for(var i = 0;i < data.length;i++){
            data[i]['columnIndex'] = true;
        }
        initRow(data);
    }

    const registDatabase = () => {
        $.CommonFunction.validateBlank();
    }

    return (
        <>
            <div className="title-area">
                データベース管理 - 新規登録
            </div>
            <div className="content-box">
                <div className="content-area">
                    <div className="input-table">
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 論理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableName"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 物理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableNameJp"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コラム</div>
                            <div className="column-td">
                                <div id="column-table" className="inner-table">
                                    <div className="inner-table-th">
                                        <span className="w-5 ta-middle"><input type="checkbox" onChange={(event) => checkAll()}/></span>
                                        <span className="w-15">論理名</span>
                                        <span className="w-15">物理名</span>
                                        <span className="w-10">タイプ</span>
                                        <span className="w-10">桁数</span>
                                        <span className="w-5">PK</span>
                                        <span className="w-5">FK</span>
                                        <span className="w-5">NN</span>
                                        <span className="w-5">AI</span>
                                        <span className="w-15">Default</span>
                                    </div>
                                    <ColumnTr
                                        rows={rows}
                                        tableRowRemove={tableRowRemove}
                                        onValueUpdate={onValueUpdate}
                                    />
                                </div>
                                <input type="button" value="追加" className="f-right ml-5" onClick={() => addRowTable()}/>
                                <input type="button" value="削除" className="f-right" onClick={() => tableRowRemove()}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="bottom-btn-area">
                <input type="button" value="登録" onClick={() => registDatabase()}/>
                <input type="button" value="戻る"/>
            </div>
        </>
    );
}

const ColumnTr = ({rows, tableRowRemove, onValueUpdate}) => {
    return rows.map((rowsData, index) => {
        const {columnIndex,columnName,columnNameJp,columnType,
            columnDigits,columnIsPk,columnIsFk,columnIsNN,columnIsAi,columnDefault} = rowsData;
        return (
            <div className="inner-table-tr" key={index}>
                <span className="w-5"><input type="checkBox" checked={columnIndex} name="columnIndex" onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnName" value={columnName} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnNameJp" value={columnNameJp} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-10">
                    <select className="w-80" name="columnType" value={columnType} onChange={(event) => onValueUpdate(index, event)}>
                        <option>int</option>
                        <option>varchar</option>
                        <option>datetime</option>
                    </select>
                </span>
                <span className="w-10"><input type="text" name="columnDigits" value={columnDigits} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsPk} name="columnIsPk" value={columnIsPk} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsFk} name="columnIsFk" value={columnIsFk} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsNN} name="columnIsNn" value={columnIsNN} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsAi} name="columnIsAi" value={columnIsAi} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnDefault" value={columnDefault} onChange={(event) => onValueUpdate(index, event)}/></span>
            </div>
        );
    });
}

export default DatabaseRegist;
