import React, { useEffect } from "react";
import { Route, Routes, useLocation } from "react-router-dom";
import AdminHome from "../AdminHome";
import DatabaseList from "../database/list";
import DatabaseRegist from "../database/regist";

const AdminContentArea = (props) => {
    const [showFlg, setShowFlg] = useState(true);
    const location = useLocation();

    useEffect(() => {
        setShowFlg(true);
        return () => {
            setShowFlg(false)
        };
      }, [location]);
      
    return (
        
            <div id="container">
                <Routes>
                    <Route path="/" element={<AdminHome/>} />
                    <Route path="/database/list" element={<DatabaseList/>} />
                    <Route path="/database/regist" element={<DatabaseRegist/>}/>
                </Routes>
            </div>
            
        
    )
    
}

export default AdminContentArea;
