import React, { useEffect, useState } from "react";

const DatabaseRegist = () => {
    const [formData, setFormData] = useState({
        tableName : '',
        tableNameJp : '',
        tableComment : '',
        mTableColumn : []
    });
    const [rows, initRow] = useState([]);
    const [formError, setFormError] = useState({
        error : false,
        errorObj : [
            {
                key : '',
                message : ''
            }
        ]
    })

    useEffect(() => {
        addRowTable();
        return () => {
            
        };
    },[]);

    const addRowTable = () => {
        const data = {
            tableColumnIndex: false,
            columnName: '',
            columnNameJp: '',
            columnType: '',
            columnDigits: '',
            columnIsPk: false,
            columnIsFk: false,
            columnIsNn: false,
            columnIsAi: false,
            columnDefault: ''
        }
        initRow([...rows, data]);
    }

    const tableRowRemove = () => {
        const dataRow = [...rows];
        for(var i = 0; i < dataRow.length; i++){
            if(dataRow[i].tableColumnIndex){
                dataRow.splice(i, 1);
            }
        }
        initRow(dataRow);
    }

    const onTableValueUpdate = (event) => {
        setFormData((prevState) => {
            var prevObj = {...prevState};
            prevObj[event.target.name] = event.target.value;
            return prevObj;
        })
    }

    const onRowValueUpdate = (i, event) => {
        const name = event.target.name;
        var value;
        if(event.target.type === 'checkbox'){
            value = event.target.checked;
        }else{
            value = event.target.value;
        }
        const data = [...rows];
        data[i][name] = value;
        observeColumnIndex(data);
        initRow(data);
        setFormData((prevState) => {
            var prevObj = {...prevState};
            prevObj['mTableColumn'] = data;
            return prevObj;
        });
    }

    const checkAll = (event) => {
        const data = [...rows];
        for(var i = 0;i < data.length;i++){
            data[i]['tableColumnIndex'] = event.target.checked;
        }
        initRow(data);
    }

    const observeColumnIndex = (data) => {
        var allCheckbox = document.getElementById('checkAll').checked;
        var isCheckAll = true;
        for(var i = 0;i < data.length;i++){
            if(data[i]['tableColumnIndex'] === false){
                isCheckAll = false;
            }
            if(!isCheckAll) break;
        }

        if(allCheckbox && !isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
        }else if(!allCheckbox && isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
        }
    }

    const registDatabase = () => {
        console.log({formData})
    }

    return (
        <>
            <div className="title-area">
                データベース管理 - 新規登録
            </div>
            <div className="content-box">
                <div className="content-area">
                    <form id="databaseRegistForm">
                    <div className="input-table">
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 論理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableName} name="tableName" onChange={(event) => onTableValueUpdate(event)}/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 物理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableNameJp} name="tableNameJp" onChange={(event) => onTableValueUpdate(event)}/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コメント</div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableComment} name="tableComment" onChange={(event) => onTableValueUpdate(event)}/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コラム</div>
                            <div className="column-td">
                                <div id="column-table" className="inner-table">
                                    <div className="inner-table-th">
                                        <span className="w-5 ta-middle"><input type="checkbox" id="checkAll" onChange={(event) => checkAll(event)}/></span>
                                        <span className="w-15">論理名</span>
                                        <span className="w-15">物理名</span>
                                        <span className="w-10">タイプ</span>
                                        <span className="w-10">桁数</span>
                                        <span className="w-5">PK</span>
                                        <span className="w-5">FK</span>
                                        <span className="w-5">NN</span>
                                        <span className="w-5">AI</span>
                                        <span className="w-15">Default</span>
                                    </div>
                                    <ColumnTr
                                        rows={rows}
                                        tableRowRemove={tableRowRemove}
                                        onRowValueUpdate={onRowValueUpdate}
                                    />
                                </div>
                                <input type="button" value="追加" className="f-right ml-5" onClick={() => addRowTable()}/>
                                <input type="button" value="削除" className="f-right" onClick={() => tableRowRemove()}/>
                            </div>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
            <div className="bottom-btn-area">
                <input type="button" value="登録" onClick={() => registDatabase()}/>
                <input type="button" value="戻る"/>
            </div>
        </>
    );
}

const ColumnTr = ({rows, tableRowRemove, onRowValueUpdate}) => {
    return rows.map((rowsData, index) => {
        const {tableColumnIndex,columnName,columnNameJp,columnType,
            columnDigits,columnIsPk,columnIsFk,columnIsNn,columnIsAi,columnDefault} = rowsData;
        return (
            <div className="inner-table-tr" key={index}>
                <span className="w-5"><input type="checkBox"  checked={tableColumnIndex} name="tableColumnIndex" onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnName" value={columnName} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnNameJp" value={columnNameJp} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-10">
                    <select className="w-80" name="columnType" value={columnType} onChange={(event) => onRowValueUpdate(index, event)}>
                        <option></option>
                        <option>int</option>
                        <option>varchar</option>
                        <option>datetime</option>
                    </select>
                </span>
                <span className="w-10"><input type="text" name="columnDigits" value={columnDigits} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsPk} name="columnIsPk" value={columnIsPk} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsFk} name="columnIsFk" value={columnIsFk} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsNn} name="columnIsNn" value={columnIsNn} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={columnIsAi} name="columnIsAi" value={columnIsAi} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="columnDefault" value={columnDefault} onChange={(event) => onRowValueUpdate(index, event)}/></span>
            </div>
        );
    });
}

export default DatabaseRegist;
