import React, { useState } from "react";
import * as $ from '../../CommonFunction';

const DatabaseRegist = () => {
    const [rows, initRow] = useState([]);
    const addRowTable = () => {
        const data = {
            columnName: '',
            columnNameJp: '',
            columnType: '',
            columnDigits: '',
            columnIsPk: '',
            columnIsNN: '',
            columnIsAi: '',
            columnDefault: ''
        }
        initRow([...rows, data]);
    }

    const registDatabase = () => {
        $.CommonFunction.validateBlank();
    }

    const addColumn = () => {
        setColumnIndex(columnIndex + 1);
    }

    return (
        <>
            <div className="title-area">
                データベース管理 - 新規登録
            </div>
            <div className="content-box">
                <div className="content-area">
                    <div className="input-table">
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 論理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableName"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 物理名</div>
                            <div className="column-td">
                                <input type="text" className="w-80" name="tableNameJp"/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コラム</div>
                            <div className="column-td">
                                <div id="column-table" className="inner-table">
                                    <div className="inner-table-th">
                                        <span className="w-20">論理名</span>
                                        <span className="w-20">物理名</span>
                                        <span className="w-10">タイプ</span>
                                        <span className="w-10">桁数</span>
                                        <span className="w-5">PK</span>
                                        <span className="w-5">NN</span>
                                        <span className="w-5">AI</span>
                                        <span className="w-20">Default</span>
                                    </div>
                                </div>
                                <input type="button" value="追加" className="f-right ml-5" onClick={() => addColumn()}/>
                                <input type="button" value="削除" className="f-right"/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="bottom-btn-area">
                <input type="button" value="登録" onClick={() => registDatabase()}/>
                <input type="button" value="戻る"/>
            </div>
        </>
    );
}

const columnTr = ({rows, tableRowRemove, onValueUpdate}) => {
    return rows.map((rowsData, index) => {
        const {columnName, columnNameJp, columnType, columnDigits, columnIsPk, columnIsNN, columnIsAi, columnDefault} = rowsData;
        return (
            <div className="inner-table-tr" key={index}>
                <span className="w-20"><input type="text" value={columnName} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-20"><input type="text" value={columnNameJp} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-10">
                    <select className="w-80" value={columnType} onChange={(event) => onValueUpdate(index, event)}>
                        <option>int</option>
                        <option>varchar</option>
                        <option>datetime</option>
                    </select>
                </span>
                <span className="w-10"><input type="text" value={columnDigits} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" value={columnIsPk} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" value={columnIsNN} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" value={columnIsAi} onChange={(event) => onValueUpdate(index, event)}/></span>
                <span className="w-20"><input type="text" value={columnDefault} onChange={(event) => onValueUpdate(index, event)}/></span>
            </div>
        );
    });
}

export default DatabaseRegist;
