import React, { useEffect, useState } from "react";
import * as $ from "../../CommonFunction";
import * as B from "../../common/BeanCollector";
import * as E from "../../common/ErrorCollector";

const DatabaseRegist = () => {
    const [formData, setFormData] = useState(B.BeanCollector.MTable);
    const [rows, initRow] = useState([B.BeanCollector.MTableColumn]);
    const error = E.ErrorCollector.init(['MTable']);
    
    useEffect(() => {
        console.log(rows);
        validateInstant(formData);
        return () => {
            
        };
    },[formData]);

    const addRowTable = () => {
        const data = B.BeanCollector.mTableColumn;
        initRow([...rows, data]);
        observeColumnIndex([...rows, data]);
    }

    const tableRowRemove = () => {
        const dataRow = [...rows];
        var newData = [];
        for(var i = 0; i < dataRow.length; i++){
            if(!dataRow[i].tableColumnIndex){
                newData.push(dataRow[i]);
            }
        }
        initRow(newData);
        observeColumnIndex(newData);
    }

    const onTableValueUpdate = (event) => {
        var nameArr = event.target.name.split('.');
        setFormData((prevState) => {
            var prevObj = {...prevState};
            var updateObj = prevObj;
            var updateValue = '';
            for(var i = 0; i < nameArr.length; i++){
                if(i === nameArr.length - 1){
                    updateValue = event.target.value;
                    updateObj[nameArr[i]] = updateValue;
                    console.log(nameArr[0]);
                    prevObj[nameArr[0]] = updateObj;
                }else{
                    updateObj = updateObj[nameArr[i]];
                }
            }
            console.log(prevObj);
            return prevObj;
        })
    }

    const onRowValueUpdate = (i, event) => {
        const name = event.target.name;
        var value;
        if(event.target.type === 'checkbox'){
            value = event.target.checked;
        }else{
            value = event.target.value;
        }
        const data = [...rows];
        data[i][name] = value;
        observeColumnIndex(data);
        initRow(data);
        setFormData((prevState) => {
            var prevObj = {...prevState};
            prevObj['mTableColumn'] = data;
            return prevObj;
        });
    }

    const checkAll = (event) => {
        const data = [...rows];
        for(var i = 0;i < data.length;i++){
            data[i]['tableColumnIndex'] = event.target.checked;
        }
        initRow(data);
    }

    const observeColumnIndex = (data) => {
        var allCheckbox = document.getElementById('checkAll').checked;
        var isCheckAll = true;
        if(data.length === 0){
            isCheckAll = false;
        }
        for(var i = 0;i < data.length;i++){
            if(data[i]['tableColumnIndex'] === false){
                isCheckAll = false;
            }
            if(!isCheckAll) break;
        }

        if(allCheckbox && !isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
        }else if(!allCheckbox && isCheckAll){
            document.getElementById('checkAll').checked = isCheckAll;
        }
    }

    const validateInstant = (data) => {
        const newData = $.CommonValidate.validateInstant(data, error);
    }

    const registDatabase = () => {
        console.log({formData})
    }

    return (
        <>
            <div className="title-area">
                データベース管理 - 新規登録
            </div>
            <div className="content-box">
                <div className="content-area">
                    <form id="databaseRegistForm">
                    <div className="input-table">
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 論理名<span className="required"></span></div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableName.value} name="tableName.value" onChange={(event) => onTableValueUpdate(event)}/>
                                <span className="error-txt"></span>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">テーブル名 - 物理名<span className="required"></span></div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableNameJp.value} name="tableNameJp.value" onChange={(event) => onTableValueUpdate(event)}/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コメント</div>
                            <div className="column-td">
                                <input type="text" className="w-80" value={formData.tableComment} name="tableComment" onChange={(event) => onTableValueUpdate(event)}/>
                            </div>
                        </div>
                        <div className="column-tr">
                            <div className="column-th">コラム</div>
                            <div className="column-td">
                                <div className="inner-table-th">
                                    <span className="w-5 ta-middle"><input type="checkbox" id="checkAll" onChange={(event) => checkAll(event)}/></span>
                                    <span className="w-15">論理名</span>
                                    <span className="w-15">物理名</span>
                                    <span className="w-10">タイプ</span>
                                    <span className="w-10">桁数</span>
                                    <span className="w-5">PK</span>
                                    <span className="w-5">FK</span>
                                    <span className="w-5">NN</span>
                                    <span className="w-5">AI</span>
                                    <span className="w-15">Default</span>
                                </div>
                                <div className="inner-table">
                                    <ColumnTr
                                        rows={rows}
                                        tableRowRemove={tableRowRemove}
                                        onRowValueUpdate={onRowValueUpdate}
                                    />
                                </div>
                                <input type="button" value="追加" className="f-right ml-5" onClick={() => addRowTable()}/>
                                <input type="button" value="削除" className="f-right" onClick={() => tableRowRemove()}/>
                            </div>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
            <div className="bottom-btn-area">
                <input type="button" value="登録" onClick={() => registDatabase()}/>
                <input type="button" value="戻る"/>
            </div>
        </>
    );
}

const ColumnTr = ({rows, tableRowRemove, onRowValueUpdate}) => {
    return rows.map((rowsData, index) => {
        const {tableColumnIndex,tableColumnName,tableColumnNameJp,tableColumnType,
            tableColumnDigits,tableColumnIsPk,tableColumnIsFk,tableColumnIsNn,tableColumnIsAi,tableColumnDefault} = rowsData;
        return (
            <div className="inner-table-tr" key={index}>
                <span className="w-5"><input type="checkBox"  checked={tableColumnIndex} name="tableColumnIndex" onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="tableColumnName" value={tableColumnName} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="tableColumnNameJp" value={tableColumnNameJp} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-10">
                    <select className="w-80" name="tableColumnType" value={tableColumnType} onChange={(event) => onRowValueUpdate(index, event)}>
                        <option></option>
                        <option>int</option>
                        <option>varchar</option>
                        <option>datetime</option>
                    </select>
                </span>
                <span className="w-10"><input type="text" name="tableColumnDigits" value={tableColumnDigits} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={tableColumnIsPk} name="tableColumnIsPk" value={tableColumnIsPk} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={tableColumnIsFk} name="tableColumnIsFk" value={tableColumnIsFk} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={tableColumnIsNn} name="tableColumnIsNn" value={tableColumnIsNn} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-5"><input type="checkbox" checked={tableColumnIsAi} name="tableColumnIsAi" value={tableColumnIsAi} onChange={(event) => onRowValueUpdate(index, event)}/></span>
                <span className="w-15"><input type="text" name="tableColumnDefault" value={tableColumnDefault} onChange={(event) => onRowValueUpdate(index, event)}/></span>
            </div>
        );
    });
}

export default DatabaseRegist;
